const { SMA } = require('../indicators');

module.exports = {
  init() {
    this.sma = new SMA(this.settings.period);
    this.addIndicator('sma', this.sma);
  },

  update(candle) {
    this.sma.update(candle.close);
  },

  check(candle) {
    if (!this.sma.isReady) return;

    const price = candle.close;
    const smaValue = this.sma.result;
    const diff = (price - smaValue) / smaValue;

    if (diff > this.settings.threshold) {
      this.advice('long');
    } else if (diff < -this.settings.threshold) {
      this.advice('short');
    }
  }
};

const { RSI } = require('../indicators');

module.exports = {
  init() {
    this.rsi = new RSI(this.settings.period);
    this.addIndicator('rsi', this.rsi);
  },

  update(candle) {
    this.rsi.update(candle.close);
  },

  check(candle) {
    if (!this.rsi.isReady) return;

    const rsiValue = this.rsi.result;

    if (rsiValue < this.settings.oversold) {
      this.advice('long');
    } else if (rsiValue > this.settings.overbought) {
      this.advice('short');
    }
  }
};
